import React, { useState, useEffect } from "react";
import Card from "../components/Card";

function HomePage() {
  const BASE_URL = "http://localhost:3000/api/jobs";

  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchJobs = async (keyword = "developer", location = "Israel") => {
    try {
      const response = await fetch(`${BASE_URL}?keyword=${keyword}&location=${location}`);
      if (!response.ok) throw new Error("Failed to fetch jobs");
      const data = await response.json();
      return data; // returns an array of jobs
    } catch (error) {
      console.error(error);
      return [];
    }
  };

  useEffect(() => {
    const getJobs = async () => {
      setLoading(true);
      const data = await fetchJobs();
      setJobs(data);
      setLoading(false);
    };
    getJobs();
  }, []);

  if (loading) return <p>Loading jobs...</p>;

  return (
    <div>
      <h1>Job List</h1>

      <Card
       title = {job.title}
       link = {job.link}
      />

      <ul>
        {jobs.map((job, index) => (
          <li key={index}>
            <a href={job.link}>
              {job.title}
            </a>
            - {job.company} ({job.location})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default HomePage;
