import React, { useState } from "react";
import Card from "../components/Card";
import Keyword from "../components/KeyWords";

function HomePage() {
  const BASE_URL = "http://localhost:3000/api/jobs";

  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [position, setPosition] = useState("developer");
  const [location, setLocation] = useState("Israel");

  // פונקציה שמבצעת חיפוש
  const handleSearch = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${BASE_URL}?keyword=${position}&location=${location}`);
      if (!response.ok) throw new Error("Failed to fetch jobs");
      const data = await response.json();
      setJobs(data);
    } catch (error) {
      console.error(error);
      setJobs([]);
    }
    setLoading(false);
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Job List</h1>

      {/* קומפוננטת החיפוש */}
      <Keyword
        position={position}
        setPosition={setPosition}
        location={location}
        setLocation={setLocation}
        handleSearch={handleSearch} // מחוברים לפונקציה
      />

      {loading && <p className="text-center mt-3">Loading jobs...</p>}

      <div className="jobCard d-flex flex-wrap gap-3 mt-3">
        {jobs.map((job, index) => (
          <Card
            key={index}
            title={job.title}
            link={job.link}
            company={job.company}
            location={job.location}
          />
        ))}
      </div>
    </div>
  );
}

export default HomePage;
