import React, { useState, useEffect } from "react";
import Card from "../components/Card";
import Keyword from "../components/KeyWords";

function HomePage() {
  const BASE_URL = "http://localhost:3000/api/jobs";
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [position, setPosition] = useState("developer");
  const [location, setLocation] = useState("Israel");


  const fetchJobs = async () => {
    try {
      const response = await fetch(`${BASE_URL}?keyword=${position}&location=${location}`);
      if (!response.ok) throw new Error("Failed to fetch jobs");
      const data = await response.json();
      return data;
    } catch (error) {
      console.error(error);
      return [];
    }
  };

  useEffect(() => {
    const getJobs = async () => {
      setLoading(true);
      const data = await fetchJobs();
      setJobs(data);
      setLoading(false);
    };
    getJobs();
  }, [position,location]);

  if (loading) return <p className="text-center mt-5">Loading jobs...</p>;

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Job List</h1>
      <Keyword
        position={position}
        setPosition={setPosition}
        location={location}
        setLocation={setLocation}
      />
      <div className="jobCard d-flex flex-wrap gap-3">
        {jobs.map((job, index) => (
          <Card
            key={index}
            title={job.title}
            link={job.link}
            company={job.company}
            location={job.location}
          />
        ))}
      </div>
    </div>
  );
}

export default HomePage;
